import React from "react";
import cn from "classnames";
import {useDrop} from "react-dnd";
import {observer} from "mobx-react-lite";
import CardView from "./CardView";
import usePosition from "../../state/hooks/usePosition";
import useHover from "../../state/hooks/useHover";

import styled from "styled-components";


function PileView({pile, y, x}) {
  const [p] = usePosition(y, x);
  const [hoverRef, isHover] = useHover();
  const [{isOver}, dropRef] = useDrop(() => ({
    accept: "CARD",
    drop: (item, monitor) => {
      // const parent = getParent(item.card, 2);
      // parent.remove(item.card);

      // if(item.card.destination.cards.length===1) {
      //    item.card.destination.removeCard(item.card);
      // }
      item.card.destination.removeCard(item.card);
      pile.put(item.card);

      return {
        place: pile,
      };
    },
    collect: monitor => ({
      isOver: !!monitor.isOver(),
    }),
  }), [x, y]);
  const turnedCards = pile.cards.filter(card => !card.isTurned);

  return (
    <div
      ref={dropRef}
      className={cn("Pile", pile.direction.name)}
      style={{left: p.x + "px", top: p.y + "px", backgroundColor: isOver ? "red" : "inherit"}}
    >
      <div ref={hoverRef}>
        {pile.cards.map((card, idx) => {
          let indentY = 0.2;
          let indentX = 0.2;
          if (!card.isTurned && isHover && turnedCards.length > 1) {
            indentY = pile.direction.name === "LeftToRight" ? 0.8 : 0.2;
            indentX = pile.direction.name === "TopToBottom" ? 0.8 : 0.2;
          }
          return (
             <div>
                <SlotPile />
                   <CardView key={idx} card={card.length>0} y={idx * indentY} x={idx * indentX}/>

             </div>
             //</SlotPile>
          );
        })}
        {isOver && (
          <div
            className={pile.top ? pile.top.Type : "Ship"}
            style={{
              position: "absolute",
              zIndex: 1,
              opacity: 0.5,
              backgroundColor: "red",
            }}
          />
        )}
      </div>
    </div>
  );
}

const SlotPileView = styled.div`
  position: absolute;
  border-width: 4px;
  border-style: solid;
  top: 0;
  left: 0;
//   top: ${({y}) => y + "px"};
//   left: ${({x}) => x + "px"};
  z-index: 2;
  border-radius: 20px;
  width: 300px;
  height: 400px;
`;

const SlotPile = observer(({ y, x ,children }) => {
  const [p] = usePosition(y, x);
//   const [{isOver, canDrop}, dropRef] = useDrop(() => ({
//     accept: "CARD",
//     drop: (item, monitor) => {
//       let a = monitor.getItem();
//       if(item.card.Type === card.Name){
//         item.card.destination.removeCard(item.card);
//         star.put(item.card, slot);
//       }
//       return {
//          place: star,
//          name: name,
//       };
//    },
//     collect: monitor => ({
//       isOver: !!monitor.isOver(),
//     }),
//   }), [x, y]);

  return (
    <SlotPileView 
      // y={p.y - 0} 
      // x={0 + p.x} 
    >
       {children}
    </SlotPileView>
  );
});

export default observer(PileView);
