import Card from "./Card.js";
import Direction from "./Direction.js";


class Pile {
  constructor(type, direction) {
    this._type = type;
    this._direction = Direction.assert(direction);
    this.absentCard = Card.assert('absent '+type);
    this._cards = [];
  }


  get label() {
   return this.absentCard.Name
  }

  get cards()     {
    return this._cards[this.size - 1] ? this._cards : [this.absentCard];
  }
  get direction() { return this._direction; }
  get type()      { return this._type; }
  get size()      { return this._cards.length; }
  get top()       { console.log(this._cards[this.size])
     return this._cards[this.size-1] ?? this.absentCard; }
//   get top()       { return this._cards[this.size-1] ?? this.absentCard; }

  get(i)          { return this._cards[i] ?? (i===0 ? this.top : null); }

  removeCard(card) {return this._cards.reduce((acc, c, index) => {
       if(card===c ){
          acc.splice(-1, 1)
      } 
      return acc;
    }, this._cards);
  }
 

  putUnder(card)  { this._cards.unshift(Card.assert(card)); }

  put(card) { 
   this._cards.push(Card.assert(card, this)); }
}

export default Pile;
