import React from "react";
import cn from "classnames";
import {useDrop} from "react-dnd";
import {observer} from "mobx-react-lite";
import CardView from "./CardView";
import usePosition from "../../state/hooks/usePosition";
import useHover from "../../state/hooks/useHover";

import styled from "styled-components";


function PileView({pile, y, x}) {
   if(pile._cards.length > 0) {
      console.log(pile.cards)
   }
  const [p] = usePosition(y, x);
  const [hoverRef, isHover] = useHover();
  const [{isOver}, dropRef] = useDrop(() => ({
    accept: "CARD",
    drop: (item, monitor) => {
      // const parent = getParent(item.card, 2);
      // parent.remove(item.card);
      // console.log(item.card.destination)
      item.card.destination.removeCard(item.card);
      pile.put(item.card);
      const destanation = item.card.destination;
      return {
        place: pile,
      };
    },
    collect: monitor => ({
      isOver: !!monitor.isOver(),
    }),
  }), [x, y]);
  const turnedCards = pile.cards.filter(card => !card.isTurned);

  return (
    <div
      ref={dropRef}
      className={cn("Pile", pile.direction.name)}
      style={{left: p.x + "px", top: p.y + "px", backgroundColor: isOver ? "red" : "inherit"}}
    >
      <div ref={hoverRef}>
        {pile.cards.map((card, idx) => {
          let indentY = 0.2;
          let indentX = 0.2;
         if(!card.isTurned && isHover && turnedCards.length > 1 && idx==0){
            indentY = pile.direction.name === "LeftToRight" ? -2 : 0;
            indentX = pile.direction.name === "TopToBottom" ? -2 : 0;
         }
         else if (!card.isTurned && isHover && turnedCards.length > 1 && idx !=0) {
            indentY = pile.direction.name === "LeftToRight" ? 0.8 : 0.2;
            indentX = pile.direction.name === "TopToBottom" ? 0.8 : 0.2;
          }
          return (
            <CardView key={idx} card={card} y={idx * indentY} x={idx * indentX}/>
          );
        })}
        {isOver && (
          <div
            className={pile.top ? pile.top.Type : "Ship"}
            style={{
              position: "absolute",
              zIndex: 1,
              opacity: 0.5,
              backgroundColor: "red",
            }}
          />
        )}
      </div>
    </div>
  );
}

const SlotPileView = styled.div`
  position: absolute;
  border-width: 4px;
  border-style: solid;
  top: ${({y}) => y + "px"};
  left: ${({x}) => x + "px"};
  z-index: 2;
  border-radius: 20px;
  width: 300px;
  height: 200px;
`;

const Slot = observer(({ y, x }) => {
// const Slot = observer(({name, star, y, x, card, slot, isActive }) => {
  const [p] = usePosition(y, x);
//   const [{isOver, canDrop}, dropRef] = useDrop(() => ({
//     accept: "CARD",
//     drop: (item, monitor) => {
//       let a = monitor.getItem();
//       if(item.card.Type === card.Name){
//         item.card.destination.removeCard(item.card);
//         star.put(item.card, slot);
//       }
//       return {
//          place: star,
//          name: name,
//       };
//    },
//     collect: monitor => ({
//       isOver: !!monitor.isOver(),
//     }),
//   }), [x, y]);

  return (
    <SlotPileView 
      ref={dropRef} 
      // className={card.Name} 
      y={p.y - 0} 
      x={18 + p.x} 
      // isOver={isOver} 
      // style={{borderColor: borderColor}}
    />
   //  <SlotView ref={dropRef} className={name} y={p.y - 94} x={18 + p.x} isOver={isOver} />
  );
});



export default observer(PileView);
